
public class AdditionOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		float f= 2345.98f;
		double d=345.56;
		String msg="hi";
		int r1=a=b;//adding 2 integer values and storing in integer
		float f1=a+f;//adding integer and float and storing in float
		double d1=f+d;//adding float and double and storing in double
		String s1= msg+f1;//adding string and float and storing in string
		
		System.out.println(r1);
		System.out.println(f1);
		System.out.println(d1);
		System.out.println(s1);
	}

}
