
public class Array2D {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int [][] arr = new int[3][2]; 
		
		
		for(int i = 0; i < arr.length; i++){
			for(int j = 0; j < arr[i].length; j++) {
				System.out.println("arr[" + i + "][" + j + "]: " + arr[i][j]);
	}
		}
}
}
