
public class CharacterPrimitive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c1='a'; //character lower case a
		char c2='E';// character upper case e
		char c3=100;// character value of 97 //decimal value of d
		char c4='\u0041'; //character value of A
		char c5=8377;//character value of rupee symbol
		System.out.println(c1);
		System.out.println(c2);
		System.out.println(c3);
		System.out.println(c4);
		System.out.println(c5);
	}

}
