
public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int counter=1;
		/*
		 * while(counter <100) { System.out.println("hello world"); counter++;
		 * System.out.println(counter); }
		 */
		while (counter++ < 50)
		{
			System.out.println("hello");
			System.out.println(counter);
		}
	}

}
